const width = window.innerWidth > 0 ? window.innerWidth : this.screen.width;
$(function () {
    // Admin Panel settings

    //****************************
    /* This is for the mini-sidebar if width is less then 1170*/
    //****************************
    var setsidebartype = function () {
        // var width =
        //   window.innerWidth > 0 ? window.innerWidth : this.screen.width;
        if (width < 1199) {
            $("#main-wrapper").attr("data-sidebartype", "mini-sidebar");
            $("#main-wrapper").addClass("mini-sidebar");
        } else {
            $("#main-wrapper").attr("data-sidebartype", "full");
            $("#main-wrapper").removeClass("mini-sidebar");
        }
    };
    $(window).ready(setsidebartype);
    $(window).on("resize", setsidebartype);
    //****************************
    /* This is for sidebartoggler*/
    //****************************
    $(".sidebartoggler").on("click", function () {
        if (width < 1000) {
            $("#main-wrapper").toggleClass("show-sidebar");
            $("#main-wrapper").toggleClass("mini-sidebar");
            if ($("#main-wrapper").hasClass("mini-sidebar")) {
                $(".sidebartoggler").prop("checked", !0);
                $("#main-wrapper").attr("data-sidebartype", "mini-sidebar");
            } else {
                $(".sidebartoggler").prop("checked", !1);
                $("#main-wrapper").attr("data-sidebartype", "full");
            }
        } else {
            $("#main-wrapper").toggleClass("mini-sidebar");
            if ($("#main-wrapper").hasClass("mini-sidebar")) {
                $(".sidebartoggler").prop("checked", !0);
                $("#main-wrapper").attr("data-sidebartype", "mini-sidebar");

                $(".title-sidebar").css("font-size", "10px");
                $(".left-sidebar").css("width", "110px");
                $(".hide-menu").css("display", "none");
                $("#search-template").css("display", "none");
                $(".sidebar-icon").css({
                    display: "flex",
                    "align-items": "center",
                    "justify-content": "center",
                    width: "100%",
                    height: "auto",
                });
                $(".card-profile").css("width", "80px");
            } else {
                $("#search-template").css("display", "block");
                $(".title-sidebar").css("font-size", "14px");
                $(".card-profile").css("width", "200px");
                $(".left-sidebar").css("width", "270px");
                $(".hide-menu").css("display", "block");
                $(".sidebar-icon").removeAttr("style");
                $(".sidebartoggler").prop("checked", !1);
                $("#main-wrapper").attr("data-sidebartype", "full");
            }
        }
    });
    // $(".sidebartoggler").on("click", function () {
    //   if (width < 1000) {
    //     $("#main-wrapper").toggleClass("show-sidebar");
    //   } else {
    //     $("#main-wrapper").toggleClass("show-sidebar");
    //   }
    // });
});
